import React, { Component } from "react";

import "./App.css";


//const formatNumber = (number) => new Intl.NumberFormat("en", { minimumFractionDigits: 2 }).format(number);
class App extends Component {
state = {

  combineData: []

}

  componentDidMount(){
const data1 = new Promise((resolve, reject) =>  


    fetch(`${process.env.PUBLIC_URL}/api/branch1.json`)
.then((r) => r.json())
.then((d) =>{

    resolve(d)
   this.setState({combineData:d.products});
    }
)


)
  }


  render() {

  console.log("-=",this.state.combineData);
    return (
      <div className="product-list">
        <label>Search Products</label>
        <input type="text" />

        <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>


     {
        this.state.combineData.map((val)=>{
        return(
         <tr key = {val.id}>
          <td> {val.name} </td>
        <td>{val.sold}</td>
         </tr>

        )})
     }


        </tbody>
        <tfoot>
          <tr>
            <td>Total</td>
            <td></td>
          </tr>
        </tfoot>
      </table>
    </div>
  );
  }
}

export default App;
