import React, { Component } from "react";

import "./App.css";


//const formatNumber = (number) => new Intl.NumberFormat("en", { minimumFractionDigits: 2 }).format(number);
class App extends Component {
state = {

  combinedUniqueData: []

}

 async componentDidMount(){
const promise1 = new Promise((resolve, reject) =>  


    fetch(`${process.env.PUBLIC_URL}/api/branch1.json`)
.then((r) => r.json())
.then((d) =>{

    resolve(d.products)
    }
)


)


const promise2 = new Promise((resolve, reject) =>  


    fetch(`${process.env.PUBLIC_URL}/api/branch2.json`)
.then((r2) => r2.json())
.then((d2) =>{

    resolve(d2.products)
  // this.setState({combineData:d.products});
    }
)

)



const promise3 = new Promise((resolve, reject) =>  


    fetch(`${process.env.PUBLIC_URL}/api/branch3.json`)
.then((r3) => r3.json())
.then((d3) =>{

    resolve(d3.products)
  // this.setState({combineData:d.products});
    }
)

)


Promise.all([promise1,promise2,promise3]).then( (dataValue)=>{
let combinedData = [ ...dataValue[0] , ...dataValue[1], ...dataValue[2]   ]
  //console.log(combinedData)

  const newCombinedData = [];
const len = combinedData.length;
for(let i=0; i < len; i++){
	if(newCombinedData[combinedData[i].id] == undefined) {


    newCombinedData[combinedData[i].id] = combinedData[i];
    }
    else {


      newCombinedData[combinedData[i].id].sold = newCombinedData[combinedData[i].id].sold + combinedData[i].sold;

      }
}
const arrFinal = [];

for(let key of newCombinedData ){
arrFinal.push(key)

}

console.log(arrFinal);

//console.log(newCombinedData.sort());
this.setState({combinedUniqueData:arrFinal})
})

  }


  render() {

  console.log("-=",this.state.combinedUniqueData.length);
    return (
      <div className="product-list">

        { this.state.combinedUniqueData.map((i)=>console.log("---",i)) }
        <label>Search Products</label>
        <input type="text" />

        <table>
        <thead>
          <tr>
            <th>id</th>
            <th>Product</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>


     {
        this.state.combinedUniqueData.map((val,i)=>{
        return(
         <tr key = {val.id}>
           <td> {i+1}</td>
          <td> {val.name} </td>
        <td>{val.sold}</td>
         </tr>

        )})
     }


        </tbody>
        <tfoot>
          <tr>
            <td>Total</td>
            <td></td>
          </tr>
        </tfoot>
      </table>
    </div>
  );
  }
}

export default App;
