    fetch(`${process.env.PUBLIC_URL}/api/branch3.json`)
.then((r3) => r3.json())
.then((d3) =>{

    resolve(d3)
  // this.setState({combineData:d.products});
    }
)

)


let [ data1, data2, data3 ] = Promise.all([promise1,promise2,promise3])

  }


  render() {

  console.log("-=",this.state.combineData);
    return (
      <div className="product-list">
        <label>Search Products</label>
        <input type="text" />

        <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>


     {
        this.state.combineData.map((val)=>{
        return(
         <tr key = {val.id}>
          <td> {val.name} </td>
        <td>{val.sold}</td>
         </tr>

        )})
     }


        </tbody>
        <tfoot>
          <tr>
            <td>Total</td>
            <td></td>
          </tr>
        </tfoot>
      </table>
    </div>
  );
  }
}

export default App;
